public class pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	        Pattern(5);
	}
	        static void Pattern(int x){
	            for(int i=x;i>=1;i--)
	            {
	                for(int j=i;j>=1;j--)
	                {
	                     System.out.print(i);
	                }
	                
	             System.out.println();  
	            }
	        }

	}
